/* Change img every 4 seconds */
var slideIndex1 = 0;
carousel();

function carousel() {
    var a;
    var b = document.getElementsByClassName("mySlides");
    for (a = 0; a < b.length; a++) {
        b[a].style.display = "none";
    }
    slideIndex1++;
    if (slideIndex1 > b.length) {
        slideIndex1 = 1
    }
    if (b[slideIndex1 - 1] != undefined) {
        b[slideIndex1 - 1].style.display = "block";
        setTimeout(carousel, 4000);
    }

}

/* Change img when click prev, next */
var slideIndex = [1, 1];
var slideId = ["mySlides"]
showSlides(1, 0);
showSlides(1, 1);

function plusSlides(n, no) {
    showSlides(slideIndex[no] += n, no);
}

function showSlides(n, no) {
    var i;
    var x = document.getElementsByClassName(slideId[no]);
    if (n > x.length) {
        slideIndex[no] = 1
    }
    if (n < 1) {
        slideIndex[no] = x.length
    }
    for (i = 0; i < x.length; i++) {
        x[i].style.display = "none";
    }
    if(x[slideIndex[no] - 1] != undefined) {
        x[slideIndex[no] - 1].style.display = "block";
    }
}
